/*Table structure for table `cmf_unit_service` */

DROP TABLE IF EXISTS `cmf_unit_service`;

CREATE TABLE `cmf_unit_service` (
  `id` int(10) unsigned NOT NULL AUTO_INCREMENT,
  `companyid` int(11) DEFAULT NULL COMMENT '所属企业id',
  `gqlx` varchar(2) DEFAULT NULL COMMENT '供求类型，1:供; 2:求',
  `servtitle` varchar(300) DEFAULT NULL COMMENT '供求标题',
  `servtype` varchar(10) DEFAULT NULL COMMENT '服务分类',
  `servstarttime` varchar(50) DEFAULT NULL COMMENT '服务开始时间',
  `servendtime` varchar(50) DEFAULT NULL COMMENT '服务截止时间',
  `servneirong` varchar(1000) DEFAULT NULL COMMENT '服务内容',
  `servfaburen` varchar(50) DEFAULT NULL COMMENT '发布人',
  `servfabutime` varchar(50) DEFAULT NULL COMMENT '发布时间',
  `servfangshi` varchar(200) DEFAULT NULL COMMENT '服务方式',
  `servshoufei` varchar(200) DEFAULT NULL COMMENT '收费模式',
  `servflow` varchar(3000) DEFAULT NULL COMMENT '服务流程',
  PRIMARY KEY (`id`)
) ENGINE=InnoDB AUTO_INCREMENT=19 DEFAULT CHARSET=utf8;

